#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int main() {
    string word;
    int count = 0;
    ifstream file("file.txt");
    if (file.is_open()) {
        while (getline(file, word)) {
            for (int i = 0; i < word.length(); i++) {
                if (word[i] == ' ') {
                    count++;
                }
            }
        }
        file.close();
    }
    cout << "The word " << word << " appears " << count << " times in the file." << endl;
    return 0;
}
