def find_longest_subsequence(s1, s2): 
  # for each character in s1 and s2 
  # if the two characters are the same 
  # then add to a list of common characters 
  # 
  # at the end, the length of the common subsequence is the longest one 

  common = set() 
  for i in range(0, len(s1)): 
    for j in range(i + 1, len(s1)): 
      if s1[i] == s2[j]: 
        common.add(s1[i]) 

  if (len(common) > 0): 
    return (len(common), s1, s2, "Found the longest subsequence of " + str(s1) + " and " + str(s2))

