```c++
#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    // 读取文本文件
    ifstream file("file.txt", ios::in);
    if (!file) {
        cout << "Failed to open file" << endl;
        return 1;
    }

    // 定义读取单词的函数
    vector<string> read_word() {
        string word;
        while (getline(file, word)) {
            if (word.empty()) {
                continue;
            }
            words.push_back(word);
        }
        return words;
    }

    // 定义计算单词出现次数的函数
    int count_word(const string& word) {
        int count = 0;
        for (int i = 0; i < words.size(); i++) {
            if (words[i] == word) {
                count++;
            }
        }
        return count;
    }

    // 计算单词出现次数
    int sum = 0;
    vector<string> words;
    while (read_word()) {
        sum += count_word(*words.back());
        words.pop_back();
    }

    // 输出结果
    cout << "单词出现次数：" << sum << endl;

    // 关闭文件
    file.close();
    return 0;
}
